function change_info(e,t){$.post(AJAX_FILE,{action:"change-info",data:e,type:t,rand:Math.random()},function(e){if(e=="Yes"){$("#info").text("Account details updated!");setTimeout(function(){$("#info").text("Account details")},1e3)}})}function logout(){$.post(AJAX_FILE,{action:"logout",rand:Math.random()},function(e){if(e=="Yes"){location.reload()}})}function notify(e,t,n){TINY.box.show({html:'<div class="box_inner"><h2>'+e+"</h2>"+t+"</div>",width:WIDTH,height:n,opacity:60,topsplit:4})}function href(e){var t=e.getAttribute("data-url");if(t=="admin"){document.location="asg-"+t}else{document.location=t}}function admin_href(e){var t=e.getAttribute("data-url");document.location="asg-admin?page="+t}function input_error(e,t){$(e).fadeIn(800,function(){if(e=="#password"){$(e)[0].type="text"}$(this).val(t).css({background:"rgba(255,0,0,0.7)",color:"#fff"})});$(e).click(function(){if(e=="#password"){$(e)[0].type="password"}$(this).val("").css({background:"#fff",color:"#444"})})}function tooltip(e){$(".tooltip").each(function(){$(this).tipsy({gravity:e,fade:true,opacity:1,title:function(){var e=$(this).attr("original-title");if(e.length>0){return e}}})})}